var createCompounder = require('./_createCompounder'),
    upperFirst = require('./upperFirst');

/**
 * Converts `string` to
 * [start case](https://en.wikipedia.org/wiki/Letter_case#Stylistic_or_specialised_usage).
 *
 * @static
 * @memberOf _
 * @since 3.1.0
 * @category String
 * @param {string} [string=''] The string to convert.
 * @returns {string} Returns the start cased string.
 * @example
 *
 * _.startCase('--foo-bar--');
 * // => 'Foo Bar'
 *
 * _.startCase('fooBar');
 * // => 'Foo Bar'
 *
 * _.startCase('__FOO_BAR__');
 * // => 'FOO BAR'
 */
var startCase = createCompounder(function(result, word, index) {
  return result + (index ? ' ' : '') + upperFirst(word);
});

module.exports = startCase;
import { computed } from 'vue'
import startCase from 'lodash/startCase'
import merge from 'lodash/merge'
import { TTableColumnSource, TableColumn, ITableItem } from '../types'

interface useColumnsProps {
  columns: TTableColumnSource[]
  items: ITableItem[]
  [prop: string]: unknown
}

export const buildTableColumn = (source: TTableColumnSource, initialIndex: number): TableColumn => {
  const input = typeof source === 'string' ? { key: source } : source

  return {
    source,
    initialIndex,
    key: input.key,
    label: input.label || startCase(input.key),
    headerTitle: input.headerTitle || input.label || startCase(input.key),
    sortable: input.sortable || false,
    sortingFn: input.sortingFn || undefined,
    alignHead: input.alignHead || 'left',
    verticalAlignHead: input.verticalAlignHead || 'middle',
    align: input.align || 'left',
    verticalAlign: input.verticalAlign || 'middle',
    width: input.width || '',
    classes: input.classes || '',
    headerClasses: input.headerClasses || '',
    style: input.style || {},
    headerStyle: input.headerStyle || {},
  }
}

const buildColumnsFromItems = (items: ITableItem[]) => {
  return Object.keys(merge({}, ...items)).map(buildTableColumn)
}

const buildNormalizedColumns = (columns: TTableColumnSource[]) => {
  return columns.map(buildTableColumn)
}

export default function useColumns (props: useColumnsProps) {
  const columnsComputed = computed(() => {
    if (props.columns.length === 0) {
      // if no column definitions provided then build them based on provided rawItems
      // e.g. if provided items look like `[{a: 1}, {b: 2}]` then there should be 2 columns: A and B
      return buildColumnsFromItems(props.items)
    } else {
      return buildNormalizedColumns(props.columns)
    }
  })

  return {
    columnsComputed,
  }
}
import { Ref, computed, watch, ref } from 'vue'
import { TableRow, ITableItem, TSelectMode } from '../types'

interface useSelectableProps {
  modelValue: ITableItem[] | undefined // selectedItems
  selectable: boolean
  selectMode: TSelectMode
  [prop: string]: unknown
}
export type TEmits = 'update:modelValue' | 'selectionChange'
export type TSelectionChange = {
  currentSelectedItems: ITableItem[],
  previousSelectedItems: ITableItem[],
}
export type TSelectableEmits = (event: TEmits, arg: ITableItem[] | TSelectionChange) => void

export default function useSelectableRow (
  paginatedRows: Ref<TableRow[]>,
  props: useSelectableProps,
  emit: TSelectableEmits,
) {
  const selectedItemsFallback = ref([] as ITableItem[])

  const selectedItemsSync = computed<ITableItem[]>({
    get () {
      if (props.modelValue === undefined) {
        return selectedItemsFallback.value
      } else {
        return props.modelValue
      }
    },

    set (modelValue) {
      if (props.modelValue === undefined) {
        selectedItemsFallback.value = modelValue
      }

      emit('update:modelValue', modelValue)
    },
  })

  const prevSelectedRowIndex = ref(-1)

  // clear all the selected rows when the `select-mode`'s value changes from multiple to single
  // (though it's safe enough to leave a selected item when changing from single to multiple
  watch(() => props.selectMode, (newSelectMode, oldSelectMode) => {
    if (newSelectMode === 'single' && oldSelectMode === 'multiple') {
      unselectAllRows()
      setPrevSelectedRowIndex(-1)
    }
  })

  // watch for rows changes (happens when filtering is applied e.g.)
  watch(paginatedRows, () => { setPrevSelectedRowIndex(-1) })

  // emit the "selection-change" event each time the selection changes
  watch(selectedItemsSync, (currentSelectedItems, previousSelectedItems) => {
    emit('selectionChange', {
      currentSelectedItems,
      previousSelectedItems,
    })
  })

  const noRowsSelected = computed(() => (
    !paginatedRows.value.some(({ source }) => selectedItemsSync.value.includes(source))
  ))

  const allRowsSelected = computed(() => {
    if (paginatedRows.value.length === 0) { return false }

    return paginatedRows.value.every(({ source }) => selectedItemsSync.value.includes(source))
  })

  const severalRowsSelected = computed(() => !noRowsSelected.value && !allRowsSelected.value)

  function isRowSelected (row: TableRow) {
    return selectedItemsSync.value.includes(row.source)
  }

  function selectAllRows () {
    selectedItemsSync.value = [...new Set([
      ...selectedItemsSync.value,
      ...paginatedRows.value.map(row => row.source),
    ])]
  }

  function unselectAllRows () {
    const paginatedRowsSource = paginatedRows.value.map(row => row.source)

    selectedItemsSync.value = selectedItemsSync.value
      .filter((row) => !paginatedRowsSource.includes(row))
  }

  // The one calling this function must guarantee that the row isn't already selected
  function selectRow (row: TableRow) {
    selectedItemsSync.value = [...selectedItemsSync.value, row.source]
  }

  function selectOnlyRow (row: TableRow) {
    selectedItemsSync.value = [row.source]
  }

  // The one calling this function must guarantee that the row is selected
  function unselectRow (row: TableRow) {
    const index = selectedItemsSync.value.findIndex(selectedItem => selectedItem === row.source)

    selectedItemsSync.value = [
      ...selectedItemsSync.value.slice(0, index),
      ...selectedItemsSync.value.slice(index + 1),
    ]
  }

  function setPrevSelectedRowIndex (rowInitialIndex: number) {
    if (rowInitialIndex === -1) {
      prevSelectedRowIndex.value = -1
    } else {
      const prevSelectedRow = paginatedRows.value.find(row => row.initialIndex === rowInitialIndex)

      prevSelectedRow
        ? prevSelectedRowIndex.value = paginatedRows.value.indexOf(prevSelectedRow)
        : prevSelectedRowIndex.value = -1
    }
  }

  function getRowsToSelect (targetIndex: number) {
    let start
    let end

    if (isRowSelected(paginatedRows.value[prevSelectedRowIndex.value])) {
      start = Math.min(prevSelectedRowIndex.value, targetIndex)
      end = Math.max(prevSelectedRowIndex.value, targetIndex)
    } else {
      start = Math.min(prevSelectedRowIndex.value + 1, targetIndex)
      end = Math.max(prevSelectedRowIndex.value - 1, targetIndex)
    }

    return paginatedRows.value.slice(start, end + 1)
  }

  function mergeSelection (rowsToSelect: TableRow[]) {
    const rowsToSelectSource = rowsToSelect.map(row => row.source)

    if (noRowsSelected.value) {
      selectedItemsSync.value = rowsToSelectSource
      return
    }

    const isInternalSelection = rowsToSelectSource.every(rowSource => selectedItemsSync.value.includes(rowSource))

    if (isInternalSelection) {
      selectedItemsSync.value = selectedItemsSync.value.filter(row => !rowsToSelectSource.includes(row))
      return
    }

    selectedItemsSync.value = [...new Set([
      ...selectedItemsSync.value,
      ...rowsToSelectSource,
    ])]
  }

  function toggleRowSelection (row: TableRow) {
    if (!props.selectable) {
      return
    }

    if (isRowSelected(row)) {
      unselectRow(row)
      props.selectMode === 'single' ? setPrevSelectedRowIndex(-1) : setPrevSelectedRowIndex(row.initialIndex)
    } else {
      props.selectMode === 'single' ? selectOnlyRow(row) : selectRow(row)
      setPrevSelectedRowIndex(row.initialIndex)
    }
  }

  function ctrlSelectRow (row: TableRow) {
    if (!props.selectable) {
      return
    }

    toggleRowSelection(row)
  }

  function shiftSelectRows (row: TableRow) {
    if (!props.selectable) {
      return
    }

    if (props.selectMode === 'single' || prevSelectedRowIndex.value === -1) {
      return toggleRowSelection(row)
    }

    const targetIndex = paginatedRows.value.indexOf(row)
    mergeSelection(getRowsToSelect(targetIndex))
    setPrevSelectedRowIndex(-1)
  }

  function toggleBulkSelection () {
    if (allRowsSelected.value) {
      unselectAllRows()
    } else {
      selectAllRows()
    }

    setPrevSelectedRowIndex(-1)
  }

  return {
    ctrlSelectRow,
    shiftSelectRows,
    toggleRowSelection,
    toggleBulkSelection,
    isRowSelected,
    noRowsSelected,
    severalRowsSelected,
    allRowsSelected,
  }
}
import { computed } from 'vue'
import { useColors } from '../../../composables/useColor'
import { safeCSSLength } from '../../../utils/css-utils'
import { TableColumn, TClassesOptions, TStyleOptions, TableCell } from '../types'
interface useStyleableProps {
  selectable: boolean
  selectedColor: string
  allowFooterSorting: boolean
  stickyHeader: boolean
  height: string | number | undefined
  [prop: string]: unknown
}

function getClasses (classes: TClassesOptions = []): string[] {
  if (typeof classes === 'function') {
    const computedClasses = classes()
    return (typeof computedClasses === 'string') ? [computedClasses] : computedClasses
  }

  return (typeof classes === 'string') ? [classes] : classes
}

function getStyles (styles: TStyleOptions = {}): Record<string, any> {
  return (typeof styles === 'function') ? styles() : styles
}

const { getColor, getFocusColor, getHoverColor, shiftHSLAColor } = useColors()

export default function useStyleable (props: useStyleableProps) {
  const color = computed(() => getColor(props.selectedColor))

  function getHeaderCSSVariables (column: TableColumn) {
    return {
      '--width': typeof column.width === 'string' ? column.width : `${column.width}px`,
      '--align': column.alignHead,
      '--vertical-align': column.verticalAlignHead,
      '--cursor': column.sortable ? 'pointer' : 'default',
    }
  }

  const rowCSSVariables = computed(() => {
    const styles: Record<string, any> = {
      '--hover-color': getHoverColor(color.value),
    }

    if (props.selectable) {
      styles['--selected-color'] = getFocusColor(color.value)
    }

    return styles
  })

  function getCellCSSVariables (cell: TableCell) {
    return {
      '--align': cell.column.align,
      '--vertical-align': cell.column.verticalAlign,
    }
  }

  function getFooterCSSVariables (column: TableColumn) {
    return {
      '--align': column.alignHead,
      '--vertical-align': column.verticalAlignHead,
      '--cursor': props.allowFooterSorting && column.sortable ? 'pointer' : 'default',
    }
  }

  function getStickyCSSVariables () {
    return {
      '--scroll-table-color': (props.height || props.stickyHeader) && shiftHSLAColor(color.value, { l: 20 }),
      '--scroll-table-height': props.height && safeCSSLength(props.height),
    }
  }

  return {
    getHeaderCSSVariables,
    rowCSSVariables,
    getCellCSSVariables,
    getFooterCSSVariables,
    getStickyCSSVariables,
    getClasses,
    getStyles,
  }
}
export const safeCSSLength = (length: string | number) => (
  typeof length === 'number' ? `${length}px` : length
)
<template>
  <va-inner-loading
    class="va-data-table"
    :class="[
      { 'va-data-table--sticky': $props.stickyHeader },
      { 'va-data-table--scroll': !!$props.height },
    ]"
    :style="{ ...getStickyCSSVariables() }"
    :loading="loading"
    :color="loadingColor"
    v-bind="componentAttributes"
  >
    <table
      class="va-data-table__table"
      :class="{ striped, selectable, hoverable, clickable }"
      v-bind="tableAttributes"
    >
      <colgroup v-if="'colgroup' in $slots">
        <slot name="colgroup" v-bind="columnsComputed" />
      </colgroup>

      <thead
        class="va-data-table__table-thead"
        :class="{ 'va-data-table__table-thead--sticky': $props.stickyHeader }">
        <slot name="headerPrepend" />

        <tr
          v-if="!hideDefaultHeader"
          class="va-data-table__table-tr"
        >
          <th
            v-if="selectable"
            :class="['va-data-table__table-th', 'va-data-table__table-cell-select']"
          >
            <va-checkbox
              v-if="selectMode === 'multiple'"
              :model-value="severalRowsSelected ? 'idl' : allRowsSelected"
              :true-value="true"
              :false-value="false"
              indeterminate-value="idl"
              indeterminate
              @update:model-value="toggleBulkSelection"
              :color="selectedColor"
            />
          </th>

          <th
            v-for="column in columnsComputed"
            :key="column.key"
            :title="column.headerTitle"
            @click.exact="column.sortable ? toggleSorting(column): () => {}"
            :style="{ ...getHeaderCSSVariables(column), ...getStyles(column.headerStyle) }"
            :class="['va-data-table__table-th', ...getClasses(column.headerClasses)]"
          >
            <div class="va-data-table__table-th-wrapper">
              <span v-if="`header(${column.key})` in $slots">
                <slot :name="`header(${column.key})`" v-bind="column" />
              </span>

              <slot v-else name="header" v-bind="column">
                <span>{{ column.label }}</span>
              </slot>

              <div
                v-if="column.sortable"
                class="va-data-table__table-th-sorting"
                @selectstart.prevent
              >
                <va-icon
                  :name="sortingOrderSync === 'asc' ? 'expand_less' : 'expand_more'"
                  size="small"
                  class="va-data-table__table-th-sorting-icon"
                  :class="{ active: sortBySync === column.key && sortingOrderSync !== null }"
                />
              </div>
            </div>
          </th>
        </tr>

        <slot name="headerAppend" />
      </thead>

      <tbody
        v-if="$slots.bodyPrepend"
        class="va-data-table__table-tbody"
        :style="rowCSSVariables"
      >
        <slot name="bodyPrepend" />
      </tbody>

      <transition
        :name="animated ? 'table-transition' : null"
        appear
        mode="out-in"
      >
        <tbody
          v-if="showNoDataHtml"
          key="showNoDataHtml"
          class="va-data-table__table-tbody"
          :style="rowCSSVariables"
        >
          <tr>
            <td
              :colspan="columnsComputed.length + (selectable ? 1 : 0)"
              v-html="noDataHtml"
              class="no-data"
            />
          </tr>
        </tbody>

        <tbody
          v-else-if="showNoDataFilteredHtml"
          key="showNoDataFilteredHtml"
          class="va-data-table__table-tbody"
          :style="rowCSSVariables"
        >
          <tr>
            <td
              :colspan="columnsComputed.length + (selectable ? 1 : 0)"
              v-html="noDataFilteredHtml"
              class="no-data"
            />
          </tr>
        </tbody>

        <tbody
          v-else
          key="showRows"
          class="va-data-table__table-tbody"
          :style="rowCSSVariables"
        >
          <transition-group
            :name="animated ? 'table-transition-rows' : null"
            appear
          >
            <tr
              v-for="row in rows"
              :key="`table-row_${row.initialIndex}`"
              class="va-data-table__table-tr"
              :class="{ selected: isRowSelected(row) }"
              @click="onRowClickHandler('row:click', $event, row)"
              @dblclick="onRowClickHandler('row:dblclick', $event, row)"
              @contextmenu="onRowClickHandler('row:contextmenu', $event, row)"
            >
              <td
                v-if="selectable"
                :class="['va-data-table__table-td', 'va-data-table__table-cell-select']"
                :key="`selectable_${row.initialIndex}`"
                @selectstart.prevent
              >
                <va-checkbox
                  :model-value="isRowSelected(row)"
                  @click.shift.exact="shiftSelectRows(row)"
                  @click.ctrl.exact="ctrlSelectRow(row)"
                  @click.exact="ctrlSelectRow(row)"
                  :color="selectedColor"
                />
              </td>

              <td
                v-for="cell in row.cells"
                :key="`table-cell_${cell.column.key + cell.rowIndex}`"
                :style="{ ...getCellCSSVariables(cell), ...getStyles(cell.column.style) }"
                :class="['va-data-table__table-td', ...getClasses(cell.column.classes)]"
              >
                <slot
                  v-if="`cell(${cell.column.key})` in $slots"
                  :name="`cell(${cell.column.key})`"
                  v-bind="{ ...cell, cells: row.cells }"
                />

                <slot v-else name="cell" v-bind="cell">
                  {{ cell.value }}
                </slot>
              </td>
            </tr>
          </transition-group>
        </tbody>
      </transition>

      <tbody
        v-if="$slots.bodyAppend"
        class="va-data-table__table-tbody"
        :style="rowCSSVariables"
      >
        <slot name="bodyAppend" />
      </tbody>

      <tfoot v-if="footerClone" class="va-data-table__table-tfoot">
        <slot name="footerPrepend" />

        <tr v-if="!hideDefaultHeader" class="va-data-table__table-tr">
          <th v-if="selectable" class="va-data-table__table-th">
            <va-checkbox
              v-if="selectMode === 'multiple'"
              :model-value="severalRowsSelected ? 'idl' : allRowsSelected"
              :true-value="true"
              :false-value="false"
              indeterminate-value="idl"
              indeterminate
              @update:model-value="toggleBulkSelection"
              :color="selectedColor"
            />
          </th>

          <th
