import { createI18n } from 'vue-i18n'
// import { createI18n } from "vue-i18n/dist/vue-i18n.esm-bundler.js";
const messages = {
    en: {
        appTitle: 'Web application',
        menu : "Menu",
        movies: "Movies",
        actors: "Actors",
        companies: "Companies"

    },
    es: {
        appTitle: 'Aplicación web',
        menu : 'Menú',
        movies: "Películas",
        actors: "Actores",
        companies: "Compañías"
    }
};
const i18n = createI18n({
    legacy: false,
    locale: 'es',
    fallbackLocale: 'es',
    globalInjection: true,
    messages
})
export default i18n
