import { createStore } from "vuex";

const store = createStore({
    state:{
        title:'Web application',
        menuVisible: false
    },
    getters:{},
    mutations:{
        change_title(state, title) {
            state.title = title
        },

        changeMenuVisibilitu(state) {
            state.menuVisible = !state.menuVisible
        }
    },
    actions:{}
});

export default store;
