  import { ref } from 'vue'

const getData = () => {


  const load = async () => {

    let data = ref([])
    let error = ref(null)

    try {
      let retData = await fetch('http://localhost:3000/movies')

      if (!retData.ok) {
        error = 'Ha habido un error'
      }

      data.value = await retData.json()

    } catch(err) {
      console.log('error', err.value)
    }
  }

  return {data, error, load}
}

export default getData;
