import { createI18n } from 'vue-i18n'
// import { createI18n } from "vue-i18n/dist/vue-i18n.esm-bundler.js";
const messages = {
    en: {
        appTitle: 'Web application',
        menu : "Menu"

    },
    es: {
        appTitle: 'Aplicación web',
        menu : 'Menú'
    }
};
const i18n = createI18n({
    legacy: false,
    locale: 'es',
    fallbackLocale: 'es',
    globalInjection: true,
    messages
})
export default i18n
